<template>
  <div class="index-panel">
    <div class="title">{{name}}</div>
    <div class="status">
      <div class="occupation"></div>
      <div>{{status_title}}</div>
      <div v-if="showTechnics" class="separate"></div>
      <div v-if="showTechnics">{{technicsName}}</div>
      <div class="separate"></div>
      <img v-if="status==1" class="light" src="/static/images/light-red.png"/>
      <img v-if="status==2" class="light" src="/static/images/light-green.png"/>
      <img v-if="status==3" class="light" src="/static/images/light-yellow.png"/>
      <div class="occupation"></div>
    </div>
    <div v-if="updateTime>0" class="update-time">更新时间: {{updateTimeText}}</div>
  </div>
</template>

<script>
  export default {
    props: {
      name: String,
      status: Number,
      status_title: String,
      showTechnics: Boolean,
      technicsName: String,
      updateTime: Number,
      updateTimeText: String
    },
    data () {
      return {}
    }
  }
</script>

